const ProfileForm = (props) => {
    const classes = useStyles();
    const handleSelect = event => {
        props.setCountry(event.target.value);
    };
    const formik = useFormik({
        initialValues: {
            handle: '',
        },
        validationSchema: Yup.object({
            handle: Yup.string()
                .required("This field is required"),
        }),
        onSubmit: values => {
            console.log(values)
            props.onSubmitPro(values)
        },
    });
    const {
        values,
        touched,
        errors,
        handleChange,
        handleBlur,
        handleSubmit
    } = formik;
    return (



        <FormControl className={classes.formControl}
            values={values.handle}
            onChange={handleChange}
            onBlur={handleBlur}
        >
            <InputLabel

                id="handle" >Select Country
            </InputLabel>
            <Select
                labelId="handle"
                id="handle"
                value={props.country}
                onChange={handleSelect}
                MenuProps={MenuProps}

            >
                {names.map((name) => (
                    <MenuItem key={name} value={name}   >
                        {name}
                    </MenuItem>
                ))}
            </Select>
            <FormHelperText className={classes.FormHelperText} >{(!props.country && errors.handle && touched.handle) && errors.handle}</FormHelperText>
        </FormControl>

    );
};
export default ProfileForm;
